```javascript
class Queue {
    constructor() {
        this.tasks = [];
    }
    task(delay, callback) {
        this.tasks.push({ delay, callback });
        return this;
    }
    start() {
        if (this.tasks.length === 0) return;
        this.tasks.reduce((s,x)=>{
            return s.then(r=>{
                return new Promise((resolve,reject)=>{
                    setTimeout(()=>{
                        x.callback();
                        resolve();
                    },x.delay);
                });
            });
        },Promise.resolve()).then(x=>{
            this.tasks=[];
            return;
        });
    }

}

new Queue()
    .task(1000, () => {
        console.log(1);
    })
    .task(2000, () => {
        console.log(2);
    })
    .task(3000, () => {
        console.log(3);
    })
    .start();



```

