            func = func.then(() => {
                return new Promise((resolve, reject) => {
                    setTimeout(() => {
                        element.callback();
                        resolve();
                    }, element.delay);
                })
            });

        }
        func.then(() => {
            console.log("ending.....");

        })
    }

}

